import React from 'react'

function Hero() {
  return (
    <section className='bg-blue-600 text-white font-monts absolute'>
    <img className='absoulte' src="/images/Screenshot 2022-01-16 at 4.46.49 AM.png" alt="" />
    <div className='flex justify-center items-center flex-col py-44'>
      <h1 className='text-6xl font-bold'>IMAGINE A PLACE...</h1>
      <p className='w-3/5 text-xl mt-8 text-center'>...where you can belong to a school club, a gaming group, or a worldwide art community. Where just you and a handful of friends can spend time together. A place that makes it easy to talk every day and hang out more often.</p>
    </div>
    </section>
  )
}

export default Hero
