import React,{useState,useEffect} from "react";
import fire from "./fire";

function Auth() {
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const [user,setUser]=useState('');


  const login = () => {
    fire
      .auth()
      .signInWithEmailAndPassword(email, password)
      .catch(err =>{
        switch (err.code) {
          case "auth/invalid-email":
          case "auth/user-not-found":
          case "auth/wrong-password":
            alert(err.message);
            break;
          default:
            alert(err.message);
            break;
        }
      })    
  }
  const signUp =() => {
    fire
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .catch(err =>{
        switch (err.code) {
          case "auth/invalid-email":
          case "auth/email-already-in-use":
          case "auth/weak-password":
            alert(err.message);
            break;
          default:
            alert(err.message);
            break;
        }
      })
  }
  const authListner = () => {
    fire
      .auth()
      .onAuthStateChanged(user => {
        if (user) {
          setUser(user);
        }
        else{
          setUser('')
        }
      });
  }
  useEffect(()=>{
  },[])

  return (
    <div className="flex flex-col w-[40%] ml-32">
      <h1 className="font-kumbh text-4xl font-bold uppercase text-center py-3">please login</h1>
      <input className="bg-green-200 outline mt-4 p-2 rounded-md" type="text" value={email} onChange={(e)=>setEmail(e.target.value)}/>
      <input className="bg-green-200 outline my-5 p-2 rounded-md" type="password" value={password} onChange={(e)=>setPassword(e.target.value)}/>
      <button className="bg-red-600 mt-10 py-3 active:scale-50 transition-all active:bg-red-400" onClick={login(email,password,)}>login</button>
      <button className="bg-blue-600 mt-5 py-3 active:scale-50 transition-all active:bg-blue-400" onClick={signUp(email,password)}>SignUp</button>
    </div>
  );
}

export default Auth;
