import React,{useState,useEffect} from "react";
// import { useHistory } from "react-router-dom";
import { supabase } from "./database/Database";

function Auth() {
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');


  const login = async (email,password,history) => {
    try{
    const {error} = await supabase.auth.signIn({email,password})
    if(error) throw error
      alert('logged in')
      history.push('/dashboard')
    }
    catch(error){
      alert('error')
    }
  }
  const signUp = async (email,password) => {
    try{
    const {error} = await supabase.auth.signUp({email,password})
    if(error) throw error
      alert('Signed up')
      // history.push('/home')
    }
    catch(error){
      alert('error.message')
    }
  }

  return (
    <div className="flex flex-col w-[40%] ml-32">
      <h1 className="font-kumbh text-4xl font-bold uppercase text-center py-3">please login</h1>
      <input className="bg-green-200 outline mt-4 p-2 rounded-md" type="text" value={email} onChange={(e)=>setEmail(e.target.value)}/>
      <input className="bg-green-200 outline my-5 p-2 rounded-md" type="password" value={password} onChange={(e)=>setPassword(e.target.value)}/>
      <button className="bg-red-600 mt-10 py-3 active:scale-50 transition-all active:bg-red-400" onClick={login(email,password,)}>login</button>
      <button className="bg-blue-600 mt-5 py-3 active:scale-50 transition-all active:bg-blue-400" onClick={signUp(email,password)}>SignUp</button>
    </div>
  );
}

export default Auth;
